import React from 'react'
import Book from './Book'

function BooksGrid({books,shelfName,updateBooksinApp}) {
    console.log("BooksGrid >> books : " +JSON.stringify(books));



    const updateBookStateInGrid = (book)=>{
      alert("BookGrid.js >> updateshelfInGrid >> " +JSON.stringify(book))

     books = books.map(b=>b.id==book.id?book:b)
      updateBooksinApp(book)
    }
  return (
    <ol className="books-grid">
   {books.filter((b)=>{return b.shelf==={shelfName}}).map((book)=>  <li key={book.id}> <Book book={book} updateBookStateInGrid = {updateBookStateInGrid}/> </li>)}

   </ol>
  )
}

export default BooksGrid
